import React, {Component} from 'react';

class Body extends Component{
    state= {
        isLoaded: false,
        repos: [],
        error: null
    };

    componentDidMount() {
        fetch("https://api.github.com/repos/SAP/aker/contributor")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        repos: result
                    });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }
    render() {
        const { error, isLoaded, repos } = this.state;
        if(error) {
            return <div>Error: {error.message}</div>;
        } else if(!isLoaded) {
            return <div>Loading...</div>;
        } else {

            return (
                <div className="_bd-container">
                    <div className="_img-container">
                        {this.state.repos.map(repo => <div className="_img-div" key={repo.id}><img className="_img"
                                                                                                   src={repo.avatar_url}/>
                        </div>)}
                    </div>
                    <div className="_aside-container">
                        <h3>Search flights </h3>
                    </div>
                </div>
            );
        }
    }
}

export default Body;
