import React, {Component} from 'react';
import {FaBeer} from 'react-icons/fa'

class Body extends Component{
    state= {
        isLoaded: false,
        repos: [
            "https://i.pinimg.com/474x/19/61/e5/1961e592eaa836b54896ace5c72f4e0e--thailand-vacation-places-in-thailand.jpg",
            "https://thumbnails.trvl-media.com/bIeSikYRAQGbR0ZgNS1wziL5IbQ=/a.travel-assets.com/mediavault.le/media/1e697c469c2d2def4bffcdf2c0e8ad939ad870f3.jpeg"
        ],
        error: null
    };

    /*componentDidMount() {
        fetch("https://api.github.com/repos/SAP/aker/contributors")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        repos: result
                    });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }*/
    render() {
        const { error, isLoaded, repos } = this.state;
        if(error) {
            return <div>Error: {error.message}</div>;
        } else if(!isLoaded) {
            return <div>Loading...</div>;
        } else {

            return (
                <div className="_bd-container">
                    <div className="_img-container">
                        {this.state.repos.map(repo => <div className="_img-div" key={repo.id}><img className="_img"
                                                                                                   src={repo.avatar_url}/>
                        </div>)}
                    </div>
                    <div className="_aside-container">
                        <h3>Search flights </h3>
                        <div className="search-container">
                            <input type="text" placeholder="Search.." name="search" />
                            <button type="submit"><FaBeer/></button>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default Body;
